import { Component , OnInit } from '@angular/core';

@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class CourseComponent  {
  archived: boolean = false;

  formations = [
    { id: 1, titre: 'Angular Avancé', price: 500, archive: false, pdfUrl: '#' },
    { id: 2, titre: 'Spring Boot', price: 700, archive: false, pdfUrl: '#' },
  ];

  onFileChange(event: any): void {
    console.log('Fichier sélectionné:', event.target.files[0]);
  }

  addFormation(titre: string, price: number): void {
    console.log('Nouvelle formation ajoutée:', titre, price);
  }


  toggleArchive(id: number, isArchived: boolean): void {
    console.log(isArchived ? 'Désarchiver' : 'Archiver', 'formation ID:', id);
  }
}
<h2>Liste des Formations</h2>

<!-- Bouton pour basculer entre les formations actives et archivées -->
<button (click)="archived = !archived">
  {{ archived ? 'Voir les formations actives' : 'Voir les formations archivées' }}
</button>

<table>
  <tr>
    <th>Titre</th>
    <th>Prix</th>
    <th>PDF</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let formation of formations">
    <td>{{ formation.titre }}</td>
    <td>{{ formation.price }} TND</td>
    <td><a href="{{ formation.pdfUrl }}" target="_blank">Voir PDF</a></td>
    <td>
      <button (click)="toggleArchive(formation.id, formation.archive)">
        {{ formation.archive ? 'Désarchiver' : 'Archiver' }}
      </button>
    </td>
  </tr>
</table>

<h2>Ajouter une Formation</h2>
<input type="text" placeholder="Titre" #titre>
<input type="number" placeholder="Prix" #price>
<input type="file" (change)="onFileChange($event)">
<button (click)="addFormation(titre.value, parseFloat(price.value))">Ajouter</button>



<!-- Navigation vers une autre page avec RouterLink -->
<a routerLink="/courses">Gérer les formations</a>
