import { Component, OnInit } from '@angular/core';
import { CourseService } from 'src/app/Modules/Courses/course.service';
import { Course } from 'src/app/Modules/Courses/models/course.model';

@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class CourseComponent implements OnInit {
  courses: Course[] = [];
  showCourseList: boolean = false;

  constructor(private courseService: CourseService) {}

  ngOnInit(): void {
    // Subscribe to the course list from the service
    this.courseService.getCourses().subscribe(courses => {
      this.courses = courses;
    });

    // Load initial data (if any)
    this.courses = [
      { id: 1, title: 'Formation Angular', description: 'Apprendre Angular', duration: 20, isArchived: false, file: null },
      { id: 2, title: 'Formation Java', description: 'Apprendre Java', duration: 30, isArchived: false, file: null },
    ];
  }

  // Afficher ou masquer la liste des formations
  toggleCourseList(): void {
    this.showCourseList = !this.showCourseList;
  }

  // Supprimer une formation
  deleteCourse(course: Course): void {
    this.courses = this.courses.filter(c => c.id !== course.id);
  }

  // Archiver une formation
  archiveCourse(course: Course): void {
    course.isArchived = true;
  }

  // Désarchiver une formation
  unarchiveCourse(course: Course): void {
    course.isArchived = false;
  }
}
