import { Component } from '@angular/core';

@Component({
  selector: 'app-ajout-formation',
  templateUrl: './ajout-formation.component.html',
  styleUrls: ['./ajout-formation.component.css']
})
export class AjoutFormationComponent {
  newCourse = {
    title: '',
    description: '',
    duration: 0
  };
  selectedFile: File | null = null;

  onFileSelected(event: any) {
    if (event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];
    }
  }

  addCourse() {
    if (!this.selectedFile) {
      alert('Veuillez sélectionner un fichier PDF.');
      return;
    }

    const formData = new FormData();
    formData.append('title', this.newCourse.title);
    formData.append('description', this.newCourse.description);
    formData.append('duration', this.newCourse.duration.toString());
    formData.append('file', this.selectedFile);

    console.log('Formation ajoutée avec succès !', this.newCourse);
    console.log('Fichier sélectionné :', this.selectedFile.name);
    alert('Formation ajoutée avec succès !');

    // Réinitialisation du formulaire
    this.resetForm();
  }

  resetForm() {
    this.newCourse = { title: '', description: '', duration: 0 };
    this.selectedFile = null;
  }
}
