import { Component , OnInit } from '@angular/core';
import { Course } from '../models/course.model';
@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class CourseComponent  {
  courses: Course[] = [];
  newCourse: Course = { id: 0, title: '', description: '', duration: 0, isArchived: false, file: null };


  showCourseList: boolean = false;  // Variable pour afficher ou masquer la liste

  constructor() {}

  ngOnInit(): void {
    // Données fictives pour l'exemple
    this.courses = [
      { id: 1, title: 'Formation Angular', description: 'Apprendre Angular', duration: 20, isArchived: false, file: null },
      { id: 2, title: 'Formation Java', description: 'Apprendre Java', duration: 30, isArchived: false, file: null },
    ];
  }

  // Ajouter une nouvelle formation
  addCourse(fileInput: any): void {
    if (this.newCourse.title && this.newCourse.description && this.newCourse.duration > 0) {
      const courseToAdd: Course = {
        ...this.newCourse,
        file: fileInput ? fileInput.files[0] : null
      };
      courseToAdd.id = this.courses.length + 1; // Définir un id unique
      this.courses.push(courseToAdd);
      // Réinitialiser les champs du formulaire
      this.newCourse = { id: 0, title: '', description: '', duration: 0, isArchived: false, file: null };
    }
  }

  // Supprimer une formation
  deleteCourse(course: Course): void {
    this.courses = this.courses.filter(c => c.id !== course.id);
  }

  // Archiver une formation
  archiveCourse(course: Course): void {
    course.isArchived = true;
  }

  // Désarchiver une formation
  unarchiveCourse(course: Course): void {
    course.isArchived = false;
  }

  // Afficher ou masquer la liste des formations
  toggleCourseList(): void {
    this.showCourseList = !this.showCourseList;
  }
}
