import { Component, OnInit } from '@angular/core';
import { CourseService } from '../services/course.service';  // ✅ Utilisation correcte du service

@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class CourseComponent implements OnInit {
  courses: any[] = [];

  constructor(private courseService: CourseService) {}

  ngOnInit(): void {
    this.loadCourses();
  }

  // Charger les formations non archivées
  loadCourses(): void {
    this.courseService.getAllNonArchivedCourses().subscribe(data => {
      this.courses = data;
    });
  }
}
