import { Component , OnInit } from '@angular/core';
import { CourseService } from './course.service';

@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class CourseComponent implements OnInit {

  courses: any[] = [];  // Propriété pour stocker les cours récupérés

  constructor(private courseService: CourseService) {}

  ngOnInit(): void {
    this.loadCourses();  // Appel à loadCourses() lorsque le composant est initialisé
  }

  loadCourses(): void {
    this.courseService.getCourses().subscribe(
      (data) => {
        console.log(data);  // Log des données récupérées pour les déboguer
        this.courses = data;  // Assigner les données de l'API à la propriété `courses`
      },
      (error) => {
        console.error('Erreur de chargement des cours', error);
      }
    );
  }
}
