import { Component, OnInit } from '@angular/core';
import { CourseService } from 'src/app/Modules/Courses/courses.service';
import { Course } from 'src/app/models/course.model';  // Assuming you've created a Course model

@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class CourseComponent implements OnInit {
  courses: Course[] = [];
  isLoading = true;
  errorMessage: string = '';

  constructor(private courseService: CourseService) {}

  ngOnInit(): void {
    this.loadCourses();
  }

  loadCourses(): void {
    this.courseService.getAllNonArchivedCourses().subscribe({
      next: (data: Course[]) => {
        this.courses = data;
        this.isLoading = false;
      },
      error: (error) => {
        this.errorMessage = 'Failed to load courses';
        this.isLoading = false;
      }
    });
  }
}
<div class="wrapper">

  <div class="main-panel">
  <div class="container">
  <h2>Gestion des Formations</h2>




  <!-- Bouton pour afficher/masquer la liste des formations -->
  <button (click)="toggleCourseList()">
    {{ showCourseList ? 'Masquer la liste' : 'Afficher la liste' }}
  </button>

  <!-- Tableau des formations si showCourseList est vrai -->
  <div *ngIf="showCourseList">
    <h3>Liste des Formations</h3>
    <table *ngIf="courses.length > 0" class="courses-table">
      <thead>
        <tr>
          <th>Titre</th>
          <th>Description</th>
          <th>Durée</th>
          <th>Statut</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let course of courses">
          <td>{{ course.title }}</td>
          <td>{{ course.description }}</td>
          <td>{{ course.duration }} heures</td>
          <td>
            <span class="badge" [class.archived]="course.isArchived">{{ course.isArchived ? 'Archivée' : 'Active' }}</span>
          </td>
          <td>
            <button *ngIf="!course.isArchived" (click)="archiveCourse(course)">Archiver</button>
            <button *ngIf="course.isArchived" (click)="unarchiveCourse(course)">Désarchiver</button>
            <button (click)="deleteCourse(course)">Supprimer</button>
          </td>
        </tr>
      </tbody>
    </table>
    <p *ngIf="courses.length === 0">Aucune formation disponible.</p>
  </div>
</div>
</div>
</div>
