import { Component } from '@angular/core';
import { CourseService } from 'src/app/Modules/Courses/courses.service';
import { Course } from 'src/app/Modules/Courses/models/course.model';

@Component({
  selector: 'app-ajout-formation',
  templateUrl: './ajout-formation.component.html',
  styleUrls: ['./ajout-formation.component.css']
})
export class AjoutFormationComponent {
  newCourse: Course = {
    id: 0,
    title: '',
    description: '',
    duration: 0,
    isArchived: false,
    file: null
  };
  selectedFile: File | null = null;

  constructor(private courseService: CourseService) {}

  onFileSelected(event: any) {
    if (event.target.files.length > 0) {
      this.selectedFile = event.target.files[0];
    }
  }

  addCourse() {
    if (!this.selectedFile) {
      alert('Veuillez sélectionner un fichier PDF.');
      return;
    }

    const courseToAdd: Course = {
      ...this.newCourse,
      file: this.selectedFile,
      id: Math.floor(Math.random() * 1000)  // Random id for the course (in real applications, the backend should handle this)
    };

    // Add the course via the service
    this.courseService.addCourse(courseToAdd);

    console.log('Formation ajoutée avec succès !', courseToAdd);
    alert('Formation ajoutée avec succès !');

    // Réinitialisation du formulaire
    this.resetForm();
  }

  resetForm() {
    this.newCourse = { id: 0, title: '', description: '', duration: 0, isArchived: false, file: null };
    this.selectedFile = null;
  }
}
