import { Component , OnInit } from '@angular/core';
import { CourseService } from './course.service';

@Component({
  selector: 'app-course',
  templateUrl: './course.component.html',
  styleUrls: ['./course.component.css']
})
export class CourseComponent implements OnInit {

  courses: any[] = [];  // Propriété pour stocker les cours récupérés

  constructor(private courseService: CourseService) {}

  ngOnInit(): void {
    this.loadCourses();  // Appel à loadCourses() lorsque le composant est initialisé
  }

  loadCourses(): void {
    this.courseService.getCourses().subscribe(
      (data) => {
        console.log(data);  // Log des données récupérées pour les déboguer
        this.courses = data;  // Assigner les données de l'API à la propriété `courses`
      },
      (error) => {
        console.error('Erreur de chargement des cours', error);
      }
    );
  }
}
<h2>Liste des Formations</h2>

<!-- Bouton pour basculer entre les formations actives et archivées -->
<button (click)="archived = !archived">
  {{ archived ? 'Voir les formations actives' : 'Voir les formations archivées' }}
</button>

<table>
  <tr>
    <th>Titre</th>
    <th>Prix</th>
    <th>PDF</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let formation of formations">
    <td>{{ formation.titre }}</td>
    <td>{{ formation.price }} TND</td>
    <td><a href="{{ formation.pdfUrl }}" target="_blank">Voir PDF</a></td>
    <td>
      <button (click)="toggleArchive(formation.id, formation.archive)">
        {{ formation.archive ? 'Désarchiver' : 'Archiver' }}
      </button>
    </td>
  </tr>
</table>

<h2>Ajouter une Formation</h2>
<input type="text" placeholder="Titre" #titre>
<input type="number" placeholder="Prix" #price>
<input type="file" (change)="onFileChange($event)">
<button (click)="addFormation(titre.value, price.value)">Ajouter</button>

<!-- Navigation vers une autre page avec RouterLink -->
<a routerLink="/formations">Gérer les formations</a>
